from django.db import models
from rental.models import *




class Customer(models.Model):
    CustomerName = models.CharField(max_length=100)
    CustomerEmail = models.EmailField(max_length=100, default='abc@gmail.com')
    CustomerPassword = models.CharField(max_length=100)
    phone=models.IntegerField(default=1234567789,null=True)

    def __str__(self):
        return self.CustomerEmail
  





class putroom(models.Model):
   

    ownername=models.CharField(max_length=100)
    owneremail=models.EmailField(max_length=100, )
    ownerphone=models.IntegerField(null=True)
    roomid=models.CharField(max_length=100)
    name=models.CharField(max_length=100)   
    image=models.ImageField(default="data:image/png;base64,")    
    location=models.CharField(max_length=200)
    Type=models.CharField(max_length=100)
    area=models.CharField(max_length=100)   
    bed=models.IntegerField(null=True)
    
    bath=models.IntegerField(null=True)
    parking=models.IntegerField(null=True)
    amenities=models.CharField(max_length=500)
    rent=models.IntegerField(null=True) 
    subimage1=models.ImageField(default="data:image/png;base64,")    
    subimage2=models.ImageField(default="data:image/png;base64,")    
    subimage3=models.ImageField(default="data:image/png;base64,")    
    description=models.CharField(max_length=500)
    

    def __str__(self):
        return self.roomid

    
       


class Contact(models.Model):
    Name = models.CharField(max_length=100)
    Email = models.EmailField(max_length=100, default='abc@gmail.com')
    Subject = models.CharField(max_length=100)
    Message = models.CharField(max_length=100)
    

    def __str__(self):
        return self.Name

    
  




    